            #region 十.将INSPECT_2D实际值传入QMS
            //1.获得INSPECT_2D实际测量的样本数量
            int actCNT = Db.Ado.GetInt(@$"SELECT COUNT(DISTINCT SAMPLEID) AS CNT FROM INSPECT_2D WHERE INSPECT_DEV2ID ='{INSPECT_DEV2ID}'");
