            string sql = @$"SELECT COLUM002ID FROM COLUM002 WHERE INSPECT_FLOWID = @INSPECT_FLOWID and COC_ATTR ='COC_ATTR_002'";
            var parameters = new SugarParameter[]
            {
                new SugarParameter("@INSPECT_FLOWID", INSPECT_FLOWID)
