            // 执行 SQL 命令
            var dataTable = Db.Ado.GetDataTable(sql, parameters);

            string COLUM002ID = string.Empty;

            //2.循环CPK - 扩展项结果集
            if (dataTable.Rows.Count > 0)
            {
                for (int i = 0; i < dataTable.Rows.Count; i++)
